# /*---------------------------------------------------------------
# * name_z
# *    given an element name, return its atomic number
# *
# * boyan boyanov 2/95
# *---------------------------------------------------------------*/


def name_z(name):
    name = name.strip(" ")
    name = name[:2]
    name = name.lower()
    name = name.capitalize()
    return element.index(name) + 1


# /*---------------------------------------------------------------
# * mcmaster
# *    given a photon energy and the fit coefficients, calculate
# *    a mcmaster x-section
# *
# * boyan boyanov 2/95
# *---------------------------------------------------------------*/
def mcmaster(ephot, *fit_coefficients):
    log_e = ephot == 1.0 and ephot or np.log(ephot)
    powers = [pow(log_e, k) for k in range(len(fit_coefficients))]
    xsec = np.dot(fit_coefficients, powers)
    return np.exp(xsec)


# /*---------------------------------------------------------------
# * mucal
# *    given an element name and a photon energy, calculate
# *    a whole slew of x-ray data.  see top of file for complete
# *    documentation.
# *
# * boyan boyanov 2/95
# *---------------------------------------------------------------*/


def mucal(name, ephot, unit="a", verbose=False):
    Z = name_z(name)

    if Z in [85, 86, 87, 88, 89, 91, 93] or Z > len(element):
        print("no data for element %s (atomic number %d)" % (name, ZZ))
        return

    if ephot < 0.0:
        print("photon energy must be non-negative")
        return

    energy = [
        k_edge[Z],
        l1_edge[Z],
        l2_edge[Z],
        l3_edge[Z],
        m_edge[Z],
        k_alpha1[Z],
        k_beta1[Z],
        l_alpha1[Z],
        l_beta1[Z],
    ]

    xsec = [0.0] * 11
    xsec[6] = at_weight[Z]
    xsec[7] = density[Z]
    if Z > 27:
        xsec[8] = l1_jump
        xsec[9] = l2_jump
    xsec[10] = l3_jump[Z]

    fluo = [0.0] * 4
    fluo[0] = k_yield[Z]
    fluo[1] = l_yield[Z][0]
    fluo[2] = l_yield[Z][1]
    fluo[3] = l_yield[Z][2]

    if ephot == 0.0:
        return energy, xsec, fluo

    if (
        (abs(k_edge[Z] - ephot) <= 0.001)
        or (abs(l1_edge[Z] - ephot) <= 0.001)  #  /* data within K edge */
        or (abs(l2_edge[Z] - ephot) <= 0.001)  #  /* data within L1 edge */
        or (abs(l3_edge[Z] - ephot) <= 0.001)  #  /* data within L2 edge */
        or (abs(m_edge[Z] - ephot) <= 0.001)  #  /* data within L3 edge */
    ):  #  /* data within M edge */
        if verbose:
            print(
                "photon energy is within 1eV of edge\nfit results may be inaccurate\n"
            )

    if ephot >= k_edge[Z]:  # /* K shell */
        shell = 1
    elif ephot >= l3_edge[Z]:  # /* L shell */
        shell = 2
    elif ephot >= m_edge[Z]:  # /* M1 subshell */
        shell = 3
    else:  # /* everything else */
        shell = 4

    if shell == 1:
        barn_photo = mcmaster(ephot, *k_fit[Z])
    elif shell == 2:
        barn_photo = mcmaster(ephot, *l_fit[Z])
        if ephot >= l1_edge[Z]:  # /* above L1-no corrections */
            pass
        elif ephot >= l2_edge[Z]:  # /* between L1 and L2 */
            barn_photo /= l1_jump
        elif ephot >= l3_edge[Z]:  # /* between L2 and L3 */
            barn_photo /= l1_jump * l2_jump
    elif shell == 3:
        barn_photo = mcmaster(ephot, *m_fit[Z])
    elif shell == 4:
        barn_photo = mcmaster(ephot, *n_fit[Z])
    else:
        print("congratulations, you have just found a bug")

    if shell > 2 and Z < 30:
        print("McMaster et al. use L-edge fits for the M edges for Z<30")
        print("WARNING: results may be inaccurate")

    barn_coh = mcmaster(ephot, *xsect_coh[Z])
    barn_ncoh = mcmaster(ephot, *xsect_ncoh[Z])
    barn_tot = barn_photo + barn_coh + barn_ncoh

    xsec[0] = barn_photo
    xsec[1] = barn_coh
    xsec[2] = barn_ncoh
    xsec[3] = barn_tot
    xsec[4] = conv_fac[Z]  #   /* conversion factor */
    xsec[5] = barn_tot * density[Z] / conv_fac[Z]

    if unit.lower() == "c":
        for k in range(4):
            xsec[k] /= xsec[4]

    return energy, xsec, fluo
